import React, { useState, useEffect } from 'react';
import { getEvents } from '../services/api';


function StudentDashboard() {
  // Get user from localStorage
  let user = {};
  try {
    user = JSON.parse(localStorage.getItem('user')) || {};
  } catch {
    user = {};
  }
  const { name = '', role = 'student', email = '', department = 'Computer Science' } = user;

  // Events state
  const [upcomingEvents, setUpcomingEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch events from backend
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const eventsRes = await getEvents();
        setUpcomingEvents(eventsRes.data || []);
      } catch (err) {
        setUpcomingEvents([]);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  // const handleRegister = async (event) => {
  //   // Registration logic (commented out)
  // };

  // const handleCancel = async (eventId) => {
  //   // Cancel registration logic (commented out)
  // };

  // Logout
  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <div style={{ padding: '2rem', maxWidth: 900, margin: 'auto' }}>
      {/* Welcome Header */}
      <h2 style={{ marginBottom: 0 }}>Welcome, {name || 'Student'} <span role="img" aria-label="wave">ðŸ‘‹</span></h2>
      <div style={{ color: '#888', marginBottom: 24 }}>Role: {role ? role.charAt(0).toUpperCase() + role.slice(1) : 'Student'}</div>

      {/* Events Section */}
      <div style={{ minWidth: 300 }}>
        <h3>All Events</h3>
        {loading ? <div>Loading events...</div> : (
          upcomingEvents.length === 0 ? <div>No events found.</div> : (
            <div>
              {upcomingEvents.map(event => (
                <div key={event.id} style={{ border: '1px solid #ddd', borderRadius: 8, padding: 16, marginBottom: 16, background: '#fafafa' }}>
                  <div style={{ fontWeight: 'bold', fontSize: 16 }}>{event.name}</div>
                  <div>Date: {event.date} &nbsp; | &nbsp; Time: {event.time}</div>
                  <div>Venue: {event.venue}</div>
                </div>
              ))}
            </div>
          )
        )}
      </div>

      {/* Profile Section */}
      <div style={{ marginTop: 40, borderTop: '1px solid #eee', paddingTop: 24, maxWidth: 400 }}>
        <h3>Profile</h3>
        <div><b>Name:</b> {name}</div>
        <div><b>Email:</b> {email}</div>
        <div><b>Department:</b> {department}</div>
      </div>

      {/* Logout Button */}
      <button
        onClick={handleLogout}
        style={{ marginTop: 32, padding: '10px 32px', background: '#d9534f', color: '#fff', border: 'none', borderRadius: 4, fontWeight: 'bold', cursor: 'pointer' }}
      >Logout</button>
    </div>
  );
}

export default StudentDashboard;
