import axios from 'axios';

const BASE_URL = "http://localhost:8081/api"; // adjust if needed

export const login = (data) => axios.post(`${BASE_URL}/auth/login`, data);
export const register = (data) => axios.post(`${BASE_URL}/auth/register`, data);
export const registerFaculty = (data) => axios.post(`${BASE_URL}/admin/add-faculty`, data);

// Fetch all upcoming events
export const getEvents = () => axios.get(`${BASE_URL}/events/all-events`);

// Faculty requests event (sends info to admin for approval)
export const requestEvent = (data) => axios.post(`${BASE_URL}/events/request-event`, data);

// Admin: get all requested events
export const getRequestedEvents = () => axios.get(`${BASE_URL}/events/all-requested-events`);

// Admin: approve a requested event
export const approveEventRequest = (requestId) => axios.post(`${BASE_URL}/events/approve-event/${requestId}`);

// Admin: reject a requested event
export const rejectEventRequest = (requestId) => axios.post(`${BASE_URL}/events/reject-event/${requestId}`);

// After admin approval, faculty creates event (final registration)
export const createEvent = (data) => axios.post(`${BASE_URL}/events/create-event`, data);

// Fetch event by ID
export const getEventById = (eventId) => axios.get(`${BASE_URL}/events/event-by-id/${eventId}`);

// Admin: modify event request with remarks
export const modifyEventRequest = (requestId, eventData) => axios.put(`${BASE_URL}/admin/modify-event/${requestId}`, eventData);

// Faculty: update an existing event request (resets status to pending for admin re-examination)
export const updateEventRequest = (requestId, eventData) => axios.put(`${BASE_URL}/events/edit-existing-event/${requestId}`, eventData);

// Admin: create event directly (goes live without approval process)
export const adminCreateEvent = (eventData) => {
  // Log data being sent for debugging
  console.log('API sending event data:', eventData);
  
  // Create a new object with the correct field names for the backend
  const backendEventData = {
    ...eventData,
    faculty_id: eventData.adminId || eventData.id, // Use adminId as faculty_id
    facultyId: eventData.adminId || eventData.id,  // Also include facultyId just in case
    created_by: 'admin'
  };
  
  console.log('Modified event data for backend:', backendEventData);
  return axios.post(`${BASE_URL}/events/create-event`, backendEventData);
};

// Fetch all events created by a specific admin
export const getEventsByAdminId = (adminId) => axios.get(`${BASE_URL}/events/all-requested-events/${adminId}`);

// Fetch all events requested by a specific faculty
export const getEventsByFacultyId = (facultyId) => axios.get(`${BASE_URL}/events/all-requested-events/${facultyId}`);

// Admin: cancel an event
export const cancelEvent = (eventId) => axios.put(`${BASE_URL}/events/cancel-event/${eventId}`);

