import React, { useState, useEffect } from 'react';
import { 
  getEvents, 
  requestEvent, 
  createEvent, 
  getRequestedEvents,
  getEventsByFacultyId
} from '../services/api';

function FacultyDashboard() {
  const [tab, setTab] = useState('view');
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({ 
    title: '', 
    date: '', 
    description: '',
    venue: ''
  });
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [myRequestId, setMyRequestId] = useState(null);
  const [polling, setPolling] = useState(false);
  const [createForm, setCreateForm] = useState({ 
    date: '', 
    description: '', 
    title: '', 
    venue: '' 
  });
  const [myEvents, setMyEvents] = useState([]);
  const [myEventsLoading, setMyEventsLoading] = useState(true);
  const [createSubmitting, setCreateSubmitting] = useState(false);
  const [createSuccess, setCreateSuccess] = useState('');
  const [createError, setCreateError] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [successMsg, setSuccessMsg] = useState('');
  const [errorMsg, setErrorMsg] = useState('');
  const [myRequests, setMyRequests] = useState([]);
  const [myRequestsLoading, setMyRequestsLoading] = useState(true);

  // Get faculty user info from localStorage
  const getUserInfo = () => {
    try {
      // First try to get faculty-specific user data
      const facultyUser = JSON.parse(localStorage.getItem('user_faculty') || '{}');
      if (facultyUser && Object.keys(facultyUser).length > 0) {
        return {
          id: facultyUser.id || facultyUser._id || '',
          name: facultyUser.name || '',
          email: facultyUser.email || '',
          role: facultyUser.role || 'faculty'
        };
      }
      
      // Fall back to checking main user if active role is faculty
      if (localStorage.getItem('lastActiveRole') === 'faculty') {
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        if (user && user.role === 'faculty') {
          return {
            id: user.id || user._id || '',
            name: user.name || '',
            email: user.email || '',
            role: user.role || ''
          };
        }
      }
      
      return { id: '', name: '', email: '', role: 'faculty' };
    } catch (error) {
      console.error('Error parsing user data:', error);
      return { id: '', name: '', email: '', role: 'faculty' };
    }
  };

  const [userInfo, setUserInfo] = useState(getUserInfo());

  // Check if the user has faculty credentials
  useEffect(() => {
    // First check if we have faculty-specific credentials
    let facultyUser = null;
    try {
      facultyUser = JSON.parse(localStorage.getItem('user_faculty') || 'null');
    } catch (e) {
      console.error('Error parsing faculty user data', e);
    }
    
    // If not found, check the main user object
    if (!facultyUser) {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      if (user && user.role === 'faculty') {
        facultyUser = user;
      }
    }
    
    // If still no faculty credentials, redirect to login
    if (!facultyUser) {
      alert('Access denied. Please login as faculty.');
      window.location.href = 'http://localhost:3000/';
      return;
    }
    
    // Only set the current active role to faculty for this session
    localStorage.setItem('lastActiveRole', 'faculty');
    
    // Store a flag to indicate this dashboard is active
    localStorage.setItem('active_dashboard', 'faculty');
    
    // Update userInfo state with the latest faculty data
    setUserInfo(getUserInfo());
  }, []);

  // Handle logout
  const handleLogout = () => {
    // Remove only faculty-specific data
    localStorage.removeItem('user_faculty');
    
    // Check if we have any other role credentials
    const hasAdmin = localStorage.getItem('user_admin');
    const hasStudent = localStorage.getItem('user_student');
    
    if (hasAdmin) {
      // Switch to admin role
      const adminUser = JSON.parse(hasAdmin);
      localStorage.setItem('user', JSON.stringify(adminUser));
      localStorage.setItem('lastActiveRole', 'admin');
      window.location.href = '/admin-dashboard';
    } else if (hasStudent) {
      // Switch to student role
      const studentUser = JSON.parse(hasStudent);
      localStorage.setItem('user', JSON.stringify(studentUser));
      localStorage.setItem('lastActiveRole', 'student');
      window.location.href = '/student-dashboard';
    } else {
      // No other roles, fully log out
      localStorage.removeItem('user');
      localStorage.removeItem('token');
      localStorage.removeItem('lastActiveRole');
      window.location.href = 'http://localhost:3000/';
    }
  };

  // Fetch all events
  useEffect(() => {
    const fetchEvents = async () => {
      setLoading(true);
      try {
        const res = await getEvents();
        setEvents(res.data || []);
      } catch {
        setEvents([]);
      }
      setLoading(false);
    };
    fetchEvents();
  }, []);

  // Fetch my events
  useEffect(() => {
    const fetchMyEvents = async () => {
      if (tab === 'myEvents' && userInfo.id) {
        setMyEventsLoading(true);
        try {
          const res = await getEventsByFacultyId(userInfo.id);
          setMyEvents(res.data || []);
        } catch {
          setMyEvents([]);
        }
        setMyEventsLoading(false);
      }
    };
    
    fetchMyEvents();
  }, [tab, userInfo.id]);

  // Fetch my requests
  useEffect(() => {
    const fetchMyRequests = async () => {
      if (tab === 'requests' && userInfo.id) {
        setMyRequestsLoading(true);
        try {
          const res = await getRequestedEvents();
          // Filter to only show this faculty's requests
          const myReqs = (res.data || []).filter(req => 
            req.facultyId?.toString() === userInfo.id?.toString()
          );
          setMyRequests(myReqs);
        } catch {
          setMyRequests([]);
        }
        setMyRequestsLoading(false);
      }
    };
    
    fetchMyRequests();
  }, [tab, userInfo.id]);

  // Handle form input
  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCreateChange = e => {
    setCreateForm({ ...createForm, [e.target.name]: e.target.value });
  };

  // Handle event request submit
  const handleSubmit = async e => {
    e.preventDefault();
    setSubmitting(true);
    setSuccessMsg('');
    setErrorMsg('');
    
    // Validation: only allow future dates
    const today = new Date();
    const selected = new Date(form.date);
    if (selected <= today) {
      setErrorMsg('Please select a future date for the event.');
      setSubmitting(false);
      return;
    }
    
    try {
      // Add faculty ID to the request
      const payload = { 
        ...form, 
        facultyId: userInfo.id 
      };
      
      const res = await requestEvent(payload);
      setSuccessMsg('Event request sent to admin for approval!');
      setForm({ title: '', date: '', description: '', venue: '' });
      setMyRequestId(res.data?.id || res.data?._id || null);
      
      // Refresh the requests list
      const requestsRes = await getRequestedEvents();
      const myReqs = (requestsRes.data || []).filter(req => 
        req.facultyId?.toString() === userInfo.id?.toString()
      );
      setMyRequests(myReqs);
      
      setPolling(true);
      setTab('requests');
    } catch (err) {
      console.error(err);
      setErrorMsg('Failed to send event request.');
    }
    
    setSubmitting(false);
  };

  // Poll for approval of requests
  useEffect(() => {
    let interval;
    if (polling) {
      interval = setInterval(async () => {
        try {
          const res = await getRequestedEvents();
          const myReqs = (res.data || []).filter(req => 
            req.facultyId?.toString() === userInfo.id?.toString()
          );
          
          // Check for any approved requests
          const approvedReq = myReqs.find(req => {
            const status = (req.status || '').toString().trim().toLowerCase();
            return status === 'approved';
          });
          
          if (approvedReq) {
            setShowCreateForm(true);
            setPolling(false);
            setCreateForm({
              title: approvedReq.title || '',
              date: approvedReq.date || approvedReq.event_date || '',
              description: approvedReq.description || '',
              venue: approvedReq.venue || '',
            });
            
            // Check if there are remarks
            if (approvedReq.remarks) {
              alert(`Your event request has been approved with remarks: ${approvedReq.remarks}`);
            } else {
              alert('Your event request has been approved! Please fill in the final event details.');
            }
            
            setTab('create');
          }
          
          // Update the requests list
          setMyRequests(myReqs);
        } catch (err) {
          console.error('Polling error:', err);
        }
      }, 5000); // Check every 5 seconds
    }
    
    return () => clearInterval(interval);
  }, [polling, userInfo.id]);

  // Handle final event creation after admin approval
  const handleCreateSubmit = async e => {
    e.preventDefault();
    setCreateSubmitting(true);
    setCreateSuccess('');
    setCreateError('');
    
    // Validation: only allow future dates
    const today = new Date();
    const selected = new Date(createForm.date);
    if (selected <= today) {
      setCreateError('Please select a future date for the event.');
      setCreateSubmitting(false);
      return;
    }
    
    try {
      // Add faculty ID to the event
      const payload = { 
        ...createForm,
        facultyId: userInfo.id,
        status: 'Approved' // Set status to approved for created events
      };
      
      await createEvent(payload);
      setCreateSuccess('Event registered successfully!');
      setCreateForm({ date: '', description: '', title: '', venue: '' });
      
      // Refresh the events lists
      const eventsRes = await getEvents();
      setEvents(eventsRes.data || []);
      
      if (userInfo.id) {
        const myEventsRes = await getEventsByFacultyId(userInfo.id);
        setMyEvents(myEventsRes.data || []);
      }
      
      setShowCreateForm(false);
      setMyRequestId(null);
      setTab('myEvents');
    } catch (err) {
      console.error(err);
      setCreateError('Failed to register event.');
    }
    
    setCreateSubmitting(false);
  };

  return (
    <div style={{ padding: '2rem', maxWidth: 900, margin: 'auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 16 }}>
        <h2>Faculty Dashboard</h2>
        <div>
          <span style={{ marginRight: 10 }}>Welcome, {userInfo.name || 'Faculty'}</span>
          <button 
            onClick={handleLogout} 
            style={{ 
              padding: '5px 10px', 
              background: '#dc3545', 
              color: 'white', 
              border: 'none', 
              borderRadius: '4px', 
              cursor: 'pointer' 
            }}
          >
            Logout
          </button>
        </div>
      </div>
      
      <div style={{ display: 'flex', gap: 16, marginBottom: 24 }}>
        <button 
          onClick={() => setTab('view')} 
          style={{ 
            background: tab === 'view' ? '#333' : '#eee', 
            color: tab === 'view' ? '#fff' : '#333', 
            border: 'none', 
            padding: '0.5rem 1.5rem', 
            cursor: 'pointer' 
          }}
        >
          View All Events
        </button>
        <button 
          onClick={() => setTab('myEvents')} 
          style={{ 
            background: tab === 'myEvents' ? '#333' : '#eee', 
            color: tab === 'myEvents' ? '#fff' : '#333', 
            border: 'none', 
            padding: '0.5rem 1.5rem', 
            cursor: 'pointer' 
          }}
        >
          My Events
        </button>
        <button 
          onClick={() => setTab('requests')} 
          style={{ 
            background: tab === 'requests' ? '#333' : '#eee', 
            color: tab === 'requests' ? '#fff' : '#333', 
            border: 'none', 
            padding: '0.5rem 1.5rem', 
            cursor: 'pointer' 
          }}
        >
          My Requests
        </button>
        <button 
          onClick={() => setTab('create')} 
          style={{ 
            background: tab === 'create' ? '#333' : '#eee', 
            color: tab === 'create' ? '#fff' : '#333', 
            border: 'none', 
            padding: '0.5rem 1.5rem', 
            cursor: 'pointer' 
          }}
        >
          Request New Event
        </button>
      </div>

      {/* View All Events */}
      {tab === 'view' && (
        <div>
          <h3>All Upcoming Events</h3>
          {loading ? <div>Loading events...</div> : (
            events.length === 0 ? <div>No events found.</div> : (
              <div>
                {events.map(event => (
                  <div 
                    key={event.id || event._id} 
                    style={{ 
                      border: '1px solid #ddd', 
                      borderRadius: 8, 
                      padding: 16, 
                      marginBottom: 16, 
                      background: '#fafafa' 
                    }}
                  >
                    <div style={{ fontWeight: 'bold', fontSize: 18 }}>{event.title || event.name}</div>
                    <div><b>Date:</b> {event.event_date || event.date}</div>
                    <div><b>Description:</b> {event.description}</div>
                    <div><b>Venue:</b> {event.venue}</div>
                  </div>
                ))}
              </div>
            )
          )}
        </div>
      )}

      {/* My Events */}
      {tab === 'myEvents' && (
        <div>
          <h3>My Events</h3>
          {myEventsLoading ? <div>Loading your events...</div> : (
            myEvents.length === 0 ? (
              <div>
                <p>You haven't created any events yet.</p>
                <button 
                  onClick={() => setTab('create')} 
                  style={{ 
                    padding: '10px 20px', 
                    background: '#007bff', 
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '4px',
                    cursor: 'pointer',
                    marginTop: '10px'
                  }}
                >
                  Request Your First Event
                </button>
              </div>
            ) : (
              <div>
                {myEvents.map(event => (
                  <div 
                    key={event.id || event._id} 
                    style={{ 
                      border: '1px solid #ddd', 
                      borderRadius: 8, 
                      padding: 16, 
                      marginBottom: 16, 
                      background: '#e6f7ff' 
                    }}
                  >
                    <div style={{ fontWeight: 'bold', fontSize: 18 }}>{event.title || event.name}</div>
                    <div><b>Date:</b> {event.event_date || event.date}</div>
                    <div><b>Description:</b> {event.description}</div>
                    <div><b>Venue:</b> {event.venue}</div>
                    <div><b>Status:</b> {event.status || 'Approved'}</div>
                    {event.status === 'canceled' && (
                      <div style={{ color: '#d9534f', fontWeight: 'bold', marginTop: 8 }}>
                        This event has been canceled.
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )
          )}
        </div>
      )}

      {/* My Requests */}
      {tab === 'requests' && (
        <div>
          <h3>My Event Requests</h3>
          {myRequestsLoading ? <div>Loading your requests...</div> : (
            myRequests.length === 0 ? (
              <div>
                <p>You haven't requested any events yet.</p>
                <button 
                  onClick={() => setTab('create')} 
                  style={{ 
                    padding: '10px 20px', 
                    background: '#007bff', 
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '4px',
                    cursor: 'pointer',
                    marginTop: '10px'
                  }}
                >
                  Request Your First Event
                </button>
              </div>
            ) : (
              <div>
                {myRequests.map(req => {
                  // Determine status styles
                  const status = (req.status || '').toString().trim().toLowerCase();
                  let statusColor = '#f0ad4e'; // default yellow for pending
                  let statusBg = '#fffbe6';
                  let statusText = 'Pending';
                  
                  if (status === 'approved') {
                    statusColor = '#28a745';
                    statusBg = '#e8f5e9';
                    statusText = 'Approved';
                  } else if (status === 'rejected') {
                    statusColor = '#dc3545';
                    statusBg = '#feecf0';
                    statusText = 'Rejected';
                  }
                  
                  return (
                    <div 
                      key={req.id || req._id} 
                      style={{ 
                        border: `1px solid ${statusColor}`, 
                        borderRadius: 8, 
                        padding: 16, 
                        marginBottom: 16, 
                        background: statusBg 
                      }}
                    >
                      <div style={{ 
                        display: 'flex', 
                        justifyContent: 'space-between', 
                        alignItems: 'center', 
                        marginBottom: 10 
                      }}>
                        <div style={{ fontWeight: 'bold', fontSize: 18 }}>{req.title}</div>
                        <div style={{ 
                          padding: '4px 10px', 
                          background: statusColor, 
                          color: 'white', 
                          borderRadius: '4px', 
                          fontWeight: 'bold' 
                        }}>
                          {statusText}
                        </div>
                      </div>
                      <div><b>Date:</b> {req.event_date || req.date}</div>
                      <div><b>Description:</b> {req.description}</div>
                      <div><b>Venue:</b> {req.venue || 'To be determined'}</div>
                      
                      {req.remarks && (
                        <div style={{ 
                          marginTop: '10px', 
                          padding: '8px', 
                          background: '#e8f4ff', 
                          borderRadius: '4px', 
                          border: '1px solid #b8daff' 
                        }}>
                          <b>Admin Remarks:</b> {req.remarks}
                        </div>
                      )}
                      
                      {status === 'approved' && (
                        <div style={{ marginTop: 10 }}>
                          <button 
                            onClick={() => {
                              setCreateForm({
                                title: req.title || '',
                                date: req.date || req.event_date || '',
                                description: req.description || '',
                                venue: req.venue || '',
                              });
                              setTab('create');
                              setShowCreateForm(true);
                            }} 
                            style={{ 
                              padding: '8px 16px', 
                              background: '#28a745', 
                              color: 'white', 
                              border: 'none', 
                              borderRadius: '4px',
                              cursor: 'pointer'
                            }}
                          >
                            Complete Event Creation
                          </button>
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            )
          )}
        </div>
      )}

      {/* Request New Event */}
      {tab === 'create' && (
        <div>
          {showCreateForm ? (
            <div>
              <h3>Create Final Event</h3>
              <p>Your event request has been approved. Please fill in the final details:</p>
              
              <form onSubmit={handleCreateSubmit} style={{ maxWidth: 500 }}>
                <div style={{ marginBottom: 12 }}>
                  <label>Event Title:</label><br />
                  <input 
                    type="text" 
                    name="title" 
                    value={createForm.title} 
                    onChange={handleCreateChange} 
                    required
                    style={{ width: '100%', padding: 8 }} 
                    placeholder="Enter event title"
                  />
                </div>
                
                <div style={{ marginBottom: 12 }}>
                  <label>Event Date:</label><br />
                  <input 
                    type="date" 
                    name="date" 
                    value={createForm.date} 
                    onChange={handleCreateChange} 
                    required
                    style={{ width: '100%', padding: 8 }} 
                  />
                </div>
                
                <div style={{ marginBottom: 12 }}>
                  <label>Venue:</label><br />
                  <input 
                    type="text" 
                    name="venue" 
                    value={createForm.venue} 
                    onChange={handleCreateChange} 
                    required
                    style={{ width: '100%', padding: 8 }} 
                    placeholder="Enter event venue"
                  />
                </div>
                
                <div style={{ marginBottom: 12 }}>
                  <label>Description:</label><br />
                  <textarea 
                    name="description" 
                    value={createForm.description} 
                    onChange={handleCreateChange} 
                    required
                    style={{ width: '100%', padding: 8, minHeight: '100px' }} 
                    placeholder="Enter event description"
                  ></textarea>
                </div>
                
                <button 
                  type="submit" 
                  disabled={createSubmitting} 
                  style={{ 
                    padding: '10px 32px', 
                    background: '#007bff', 
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '4px',
                    cursor: createSubmitting ? 'not-allowed' : 'pointer',
                    opacity: createSubmitting ? 0.7 : 1,
                    fontWeight: 'bold'
                  }}
                >
                  {createSubmitting ? 'Creating...' : 'Create Event'}
                </button>
              </form>
              
              {createSuccess && (
                <div style={{ 
                  color: 'green', 
                  marginTop: 16, 
                  padding: '10px',
                  background: '#d4edda',
                  borderRadius: '4px',
                  border: '1px solid #c3e6cb'
                }}>
                  {createSuccess}
                </div>
              )}
              
              {createError && (
                <div style={{ 
                  color: '#721c24', 
                  marginTop: 16, 
                  padding: '10px',
                  background: '#f8d7da',
                  borderRadius: '4px',
                  border: '1px solid #f5c6cb'
                }}>
                  {createError}
                </div>
              )}
            </div>
          ) : (
            <div>
              <h3>Request New Event</h3>
              <p>Submit your event request for admin approval:</p>
              
              <form onSubmit={handleSubmit} style={{ maxWidth: 500 }}>
                <div style={{ marginBottom: 12 }}>
                  <label>Event Title:</label><br />
                  <input 
                    type="text" 
                    name="title" 
                    value={form.title} 
                    onChange={handleChange} 
                    required
                    style={{ width: '100%', padding: 8 }} 
                    placeholder="Enter event title"
                  />
                </div>
                
                <div style={{ marginBottom: 12 }}>
                  <label>Proposed Date:</label><br />
                  <input 
                    type="date" 
                    name="date" 
                    value={form.date} 
                    onChange={handleChange} 
                    required
                    style={{ width: '100%', padding: 8 }} 
                  />
                </div>
                
                <div style={{ marginBottom: 12 }}>
                  <label>Venue:</label><br />
                  <input 
                    type="text" 
                    name="venue" 
                    value={form.venue} 
                    onChange={handleChange} 
                    required
                    style={{ width: '100%', padding: 8 }} 
                    placeholder="Enter proposed venue"
                  />
                </div>
                
                <div style={{ marginBottom: 12 }}>
                  <label>Description:</label><br />
                  <textarea 
                    name="description" 
                    value={form.description} 
                    onChange={handleChange} 
                    required
                    style={{ width: '100%', padding: 8, minHeight: '100px' }} 
                    placeholder="Enter event description and purpose"
                  ></textarea>
                </div>
                
                <button 
                  type="submit" 
                  disabled={submitting} 
                  style={{ 
                    padding: '10px 32px', 
                    background: '#007bff', 
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '4px',
                    cursor: submitting ? 'not-allowed' : 'pointer',
                    opacity: submitting ? 0.7 : 1,
                    fontWeight: 'bold'
                  }}
                >
                  {submitting ? 'Submitting...' : 'Submit Request'}
                </button>
              </form>
              
              {successMsg && (
                <div style={{ 
                  color: 'green', 
                  marginTop: 16, 
                  padding: '10px',
                  background: '#d4edda',
                  borderRadius: '4px',
                  border: '1px solid #c3e6cb'
                }}>
                  {successMsg}
                </div>
              )}
              
              {errorMsg && (
                <div style={{ 
                  color: '#721c24', 
                  marginTop: 16, 
                  padding: '10px',
                  background: '#f8d7da',
                  borderRadius: '4px',
                  border: '1px solid #f5c6cb'
                }}>
                  {errorMsg}
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default FacultyDashboard;
