// src/pages/Login.js
import React, { useState } from 'react';
import { login, register } from '../services/api'; // ðŸ‘ˆ import register function


function Login() {
  const [tab, setTab] = useState('login'); // 'login' or 'register'
  const [role, setRole] = useState('student');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');

  const handleLogin = async () => {
    try {
      // Log the role the user is trying to log in with
      console.log('Login attempt with role:', role);

      const res = await login({ role, email, password });

      // Log the response data
      console.log('Login response:', res.data);

      // Verify role in the response matches requested role
      if (res.data && res.data.role !== role) {
        console.warn('Role mismatch! Requested:', role, 'Received:', res.data.role);
        alert(`Warning: Your account has ${res.data.role} privileges, not ${role} as selected.`);
      }

      // Generate a unique session ID for this login
      const sessionId = new Date().getTime() + '_' + Math.random().toString(36).substring(2, 15);

      // Store user data with a unique session key
      localStorage.setItem(`user_${sessionId}`, JSON.stringify(res.data));

      // Also store the current active session ID
      localStorage.setItem('currentSession', sessionId);

      // Clear any existing role-specific storage
      localStorage.removeItem('user_admin');
      localStorage.removeItem('user_faculty');
      localStorage.removeItem('user');

      // Clear any previous active role indicator
      localStorage.removeItem('lastActiveRole');

      // Store in role-specific storage and set lastActiveRole
      if (role === 'admin' || (res.data && res.data.role === 'admin')) {
        console.log('Storing admin-specific user data');
        localStorage.setItem('user_admin', JSON.stringify(res.data));
        localStorage.setItem('lastActiveRole', 'admin');
      } else if (role === 'faculty' || (res.data && res.data.role === 'faculty')) {
        console.log('Storing faculty-specific user data');

        // Create a unique key for this faculty using their email or ID
        const facultyId = res.data.id || res.data._id || '';
        const facultyEmail = res.data.email || '';
        const facultyKey = `user_faculty_${facultyId}_${facultyEmail.replace(/[^a-zA-Z0-9]/g, '')}`;

        // Store the faculty data with their unique key
        localStorage.setItem(facultyKey, JSON.stringify(res.data));

        // Also store the key in the session data so we can find it later
        localStorage.setItem(`faculty_key_${sessionId}`, facultyKey);

        // Keep legacy storage for backward compatibility
        localStorage.setItem('user_faculty', JSON.stringify(res.data));

        localStorage.setItem('lastActiveRole', 'faculty');
      } else {
        console.log('Storing student user data');
        localStorage.setItem('user', JSON.stringify(res.data));
        localStorage.setItem('lastActiveRole', 'student');
      }

      // Log what we stored
      console.log('Stored session ID:', sessionId);
      console.log('Stored user data under key:', `user_${sessionId}`);

      alert("Login successful!");

      // Get actual role from server response, with fallback to selected role
      const actualRole = (res.data && res.data.role) || role;

      // Redirect based on actual role
      if (actualRole === 'admin') {
        window.location.href = "/admin-dashboard";
      } else if (actualRole === 'faculty') {
        window.location.href = "/faculty-dashboard";
      } else {
        window.location.href = "/student-dashboard";
      }
    } catch (error) {
      alert("Login failed. Check your credentials.");
    }
  };

  const handleRegister = async () => {
    try {
      const res = await register({ name, role: "student", email, password });
      alert("Registration successful! Please login.");
      setTab('login');
      setEmail('');
      setPassword('');
      setName('');
    } catch (error) {
      alert("Registration failed. Email might be already in use.");
    }
  };

  return (
    <div style={{ padding: "2rem", maxWidth: 400, margin: 'auto' }}>
      <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 24 }}>
        <button
          onClick={() => setTab('login')}
          style={{ background: tab === 'login' ? '#333' : '#eee', color: tab === 'login' ? '#fff' : '#333', border: 'none', padding: '0.5rem 1.5rem', cursor: 'pointer', marginRight: 8 }}
        >
          Login
        </button>
        <button
          onClick={() => setTab('register')}
          style={{ background: tab === 'register' ? '#333' : '#eee', color: tab === 'register' ? '#fff' : '#333', border: 'none', padding: '0.5rem 1.5rem', cursor: 'pointer' }}
        >
          Register (Student)
        </button>
      </div>

      {tab === 'login' ? (
        <>
          <h2>Login</h2>
          <select value={role} onChange={e => setRole(e.target.value)} style={{ width: '100%', padding: 8 }}>
            <option value="admin">Admin</option>
            <option value="faculty">Faculty</option>
            <option value="student">Student</option>
          </select><br /><br />
          <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} style={{ width: '100%', padding: 8 }} /><br /><br />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} style={{ width: '100%', padding: 8 }} /><br /><br />
          <button onClick={handleLogin} style={{ width: '100%', padding: 10 }}>Login</button>
        </>
      ) : (
        <>
          <h2>Student Registration</h2>
          <input type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)} style={{ width: '100%', padding: 8 }} /><br /><br />
          <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} style={{ width: '100%', padding: 8 }} /><br /><br />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} style={{ width: '100%', padding: 8 }} /><br /><br />
          <button onClick={handleRegister} style={{ width: '100%', padding: 10 }}>Register</button>
        </>
      )}
    </div>
  );
}

export default Login;
