import React, { useState, useEffect } from 'react';
import { getEvents, getRegisteredEvents, registerForEvent, cancelEventRegistration } from '../services/api';


function StudentDashboard() {
  // Get user from localStorage
  let user = {};
  try {
    user = JSON.parse(localStorage.getItem('user')) || {};
  } catch {
    user = {};
  }
  const { name = '', role = 'student', email = '', department = 'Computer Science' } = user;

  // Events state
  const [upcomingEvents, setUpcomingEvents] = useState([]);
  const [registeredEvents, setRegisteredEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [regLoading, setRegLoading] = useState(false);
  const [cancelLoading, setCancelLoading] = useState(false);

  // Fetch events from backend
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const eventsRes = await getEvents();
        setUpcomingEvents(eventsRes.data || []);
        // Fetch registered events for this student (by email)
        const regRes = await getRegisteredEvents(email);
        setRegisteredEvents(regRes.data || []);
      } catch (err) {
        setUpcomingEvents([]);
        setRegisteredEvents([]);
      }
      setLoading(false);
    };
    if (email) fetchData();
  }, [email]);

  // Register for event (API)
  const handleRegister = async (event) => {
    setRegLoading(true);
    try {
      await registerForEvent({ eventId: event.id, student: email });
      // Refresh registered events
      const regRes = await getRegisteredEvents(email);
      setRegisteredEvents(regRes.data || []);
    } catch (err) {
      alert('Failed to register for event.');
    }
    setRegLoading(false);
  };

  // Cancel registration (API)
  const handleCancel = async (eventId) => {
    setCancelLoading(true);
    try {
      await cancelEventRegistration({ eventId, student: email });
      // Refresh registered events
      const regRes = await getRegisteredEvents(email);
      setRegisteredEvents(regRes.data || []);
    } catch (err) {
      alert('Failed to cancel registration.');
    }
    setCancelLoading(false);
  };

  // Logout
  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <div style={{ padding: '2rem', maxWidth: 900, margin: 'auto' }}>
      {/* Welcome Header */}
      <h2 style={{ marginBottom: 0 }}>Welcome, {name || 'Student'} <span role="img" aria-label="wave">ðŸ‘‹</span></h2>
      <div style={{ color: '#888', marginBottom: 24 }}>Role: {role ? role.charAt(0).toUpperCase() + role.slice(1) : 'Student'}</div>

      {/* Dashboard Sections */}
      <div style={{ display: 'flex', gap: 32, flexWrap: 'wrap' }}>
        {/* Upcoming Events */}
        <div style={{ flex: 1, minWidth: 300 }}>
          <h3>Upcoming Events</h3>
          {loading ? <div>Loading events...</div> : (
            upcomingEvents.length === 0 ? <div>No upcoming events.</div> : (
              <div>
                {upcomingEvents.map(event => (
                  <div key={event.id} style={{ border: '1px solid #ddd', borderRadius: 8, padding: 16, marginBottom: 16, background: '#fafafa' }}>
                    <div style={{ fontWeight: 'bold', fontSize: 16 }}>{event.name}</div>
                    <div>Date: {event.date} &nbsp; | &nbsp; Time: {event.time}</div>
                    <div>Venue: {event.venue}</div>
                    <button
                      style={{ marginTop: 8, padding: '6px 16px', background: '#333', color: '#fff', border: 'none', borderRadius: 4, cursor: regLoading ? 'not-allowed' : 'pointer', opacity: regLoading ? 0.6 : 1 }}
                      onClick={() => handleRegister(event)}
                      disabled={regLoading}
                    >Register</button>
                  </div>
                ))}
              </div>
            )
          )}
        </div>

        {/* Registered Events */}
        <div style={{ flex: 1, minWidth: 300 }}>
          <h3>My Registered Events</h3>
          {loading ? <div>Loading registered events...</div> : (
            registeredEvents.length === 0 ? <div>No registered events.</div> : (
              <div>
                {registeredEvents.map(event => (
                  <div key={event.id} style={{ border: '1px solid #cce5ff', borderRadius: 8, padding: 16, marginBottom: 16, background: '#eaf6ff' }}>
                    <div style={{ fontWeight: 'bold', fontSize: 16 }}>{event.name}</div>
                    <div>Date: {event.date} &nbsp; | &nbsp; Time: {event.time}</div>
                    <div>Venue: {event.venue}</div>
                    <div>Status: <span style={{ color: event.status === 'Confirmed' ? 'green' : '#888' }}>{event.status}</span></div>
                    <button
                      style={{ marginTop: 8, padding: '6px 16px', background: '#fff', color: '#d9534f', border: '1px solid #d9534f', borderRadius: 4, cursor: cancelLoading ? 'not-allowed' : 'pointer', opacity: cancelLoading ? 0.6 : 1 }}
                      onClick={() => handleCancel(event.id)}
                      disabled={cancelLoading}
                    >Cancel Registration</button>
                  </div>
                ))}
              </div>
            )
          )}
        </div>
      </div>

      {/* Profile Section */}
      <div style={{ marginTop: 40, borderTop: '1px solid #eee', paddingTop: 24, maxWidth: 400 }}>
        <h3>Profile</h3>
        <div><b>Name:</b> {name}</div>
        <div><b>Email:</b> {email}</div>
        <div><b>Department:</b> {department}</div>
      </div>

      {/* Logout Button */}
      <button
        onClick={handleLogout}
        style={{ marginTop: 32, padding: '10px 32px', background: '#d9534f', color: '#fff', border: 'none', borderRadius: 4, fontWeight: 'bold', cursor: 'pointer' }}
      >Logout</button>
    </div>
  );
}

export default StudentDashboard;
